import { Button, TextField } from "@mui/material";
import React from "react";

class AddPost extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      userPost: {
        caption: null,
        location: null,
      },
    };
    this.setPostData = this.setPostData.bind(this);
  }
  componentDidUpdate() {}
  setPostData = (e) => {
    this.setState({
      userPost: {
        ...this.state.userPost,
        [e.target.name]: e.target.value,
      },
    });
  };
  render() {
    return (
      <>
        <div className="flex flex-col">
          <h2 className="">Add Post</h2>
          <div className="px-10 mt-10 flex flex-col">
            <TextField
              onChange={this.setPostData}
              name="caption"
              label="Caption"
              error={!this.state.userPost.caption}
              helperText="Field is required"
            />
            <TextField
              onChange={this.setPostData}
              name="location"
              label="Location"
              error={!this.state.userPost.location}
              helperText="Field is required"
            />
            <Button className="w-2/5 self-center" variant="outlined">ADD</Button>
          </div>
        </div>
      </>
    );
  }
}
export default AddPost;
