import React from "react";
import { Link } from "react-router-dom";

class Dashboard extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      userPosts: [],
      dummy: ["1", "ooo"],
      userPostImg:null
    };
  }
  componentDidMount() {
    const localUserPosts = JSON.parse(localStorage.getItem("localUserPost"));
    localUserPosts.map((items)=>{
      items.postImage = URL.createObjectURL(items.postImage)

    })
    this.setState({
      userPosts: localUserPosts,
    });


  }
  componentDidUpdate() {
    console.log("this.state.userPost: ", this.state);
  }
  render() {
    return (
      <>
        <h2 className="mb-6">Dashboard</h2>
        {this.state.userPosts.map((item, index) => {

          return (
            <div key={index} className="my-8 border py-5 px-30 w-10/12 mx-auto">
              <img src={item.postImage} />
              <p>{item.caption}</p>
              <p>{item.location}</p>
            </div>
          );
        })}

        <p className="mt-10">
          {" "}
          <Link className="p-1 bg-gray-500 " to="AddPost">
            Add Posts
          </Link>
        </p>
      </>
    );
  }
}
export default Dashboard;
