import { Button, TextField } from "@mui/material";
import React from "react";

class AddPost extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      userPost: {
        caption: null,
        location: null,
      },
      userPosts: [],
    };
    this.setPostData = this.setPostData.bind(this);
    this.setPostData = this.setPostData.bind(this);
  }
  shouldComponentUpdate(nextProps) {

    if (nextProps.value !== this.props.value) {
      return true;
    } else {
      return false;
    }
  }
  componentDidUpdate() {
    const localUserPost = JSON.parse(localStorage.getItem("localUserPost"));
    this.setState({
      userPosts: [localUserPost],
    });
    // console.log("this.state.userPosts:", this.state.userPosts);
    console.log("userPosts did update: ", this.state.userPosts);
  }
  addPostClick = () => {
    const { caption, location } = this.state.userPost;
    if (caption && location) {
      // SET SINGLE USERPOST INTO LOCALSTORAGE
      localStorage.setItem(
        "localUserPost",
        JSON.stringify(this.state.userPost)
      );

      this.state.userPost.caption = "";
      this.state.userPost.location = "";

      // window.location.href="./"
    } else {
      alert("Please fill the details");
    }
  };
  setPostData = (e) => {
    this.setState({
      userPost: {
        ...this.state.userPost,
        [e.target.name]: e.target.value,
      },
    });
  };
  render() {
    return (
      <>
        <div className="flex flex-col">
          <h2 className="">Add Post</h2>
          <div className="px-10 mt-10 flex flex-col">
            <TextField
              onChange={this.setPostData}
              name="caption"
              label="Caption"
              error={!this.state.userPost.caption}
              helperText="Field is required"
              value={this.state.userPost.caption || ""}
            />
            <TextField
              onChange={this.setPostData}
              name="location"
              label="Location"
              error={!this.state.userPost.location}
              helperText="Field is required"
              value={this.state.userPost.location || ""}
            />
            <Button
              onClick={this.addPostClick}
              className="w-2/5 self-center"
              variant="outlined"
            >
              ADD
            </Button>
          </div>
        </div>
      </>
    );
  }
}
export default AddPost;
