import { TextField } from "@mui/material";
import React from "react";

class AddPost extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      userPost: {
        caption: null,
        location: null,
      },
    };
    this.setPostData = this.setPostData.bind(this);
  }
  componentDidUpdate() {
    this.setPostData = (e) => {
      this.setState({
        userPost: {
          ...this.state.userPost,
          [e.target.name]: e.target.value,
        },
      });
    };
  }
  render() {
    return (
      <>
        <h2 className="">Add Post</h2>
        <TextField onChange={this.setPostData()} name="caption" label="Caption" />
        <TextField
          onChange={this.setPostData()}
          name="location"
          label="Location"
        />
      </>
    );
  }
}
export default AddPost;
