import { Button } from "@mui/material";
import React from "react";
import { Link } from "react-router-dom";

class Dashboard extends React.PureComponent {
  constructor(props) {
    super(props);
    let userData = [{
      caption:null,
      location:null,
      heartCount:0
    }]
    this.state = {
      userPosts: userData,
      dummy: ["1", "ooo"],
      userPostImg: null,
    };
    this.heartClick = this.heartClick.bind(this);
  }
  componentDidMount() {
    const localUserPosts = JSON.parse(localStorage.getItem("localUserPost"));
    // localUserPosts.map((items) => {
    //   return items.postImage = URL.createObjectURL(items.postImage);
    // });
    this.setState({
      userPosts: localUserPosts,
    });
  }
  componentDidUpdate() {
    console.log("this.state.userPost: ", this.state);
  }

  heartClick = () => {
    // let count = 0;
    this.setState({
      userPosts: {
        heartCount: this.state.heartCount + 1,
      },
    });
  };
  render() {
    return (
      <>
        <h2 className="mb-6">Dashboard</h2>

        {this.state.userPosts?.map((item, index) => {
          // URL.createObjectURL(item.postImage)
          return (
            <div
              key={index}
              className="my-8 border px-30 w-8/12 mx-auto flex flex-col"
            >
              <img src={item.postImage} alt="" />
              <p
                style={{
                  fontSize: "10px",
                  textAlign: "left",
                  marginLeft: "5px",
                }}
              >
                {item.location}
              </p>
              <span style={{ marginTop: "170px" }}>
                <span
                  style={{
                    marginLeft: "5px",
                    float: "left",
                    cursor: "pointer",
                  }}
                  onClick={this.heartClick}
                >
                  &hearts;&nbsp;{this.state.heartCount}
                </span>
                <span
                  style={{
                    marginRight: "5px",
                    float: "right",
                    fontSize: "12px",
                    marginTop: "10px",
                  }}
                >
                  comment?
                </span>
              </span>
              <p
                style={{
                  fontSize: "10px",
                  textAlign: "left",
                  marginLeft: "5px",
                }}
              >
                {item.caption}
              </p>
            </div>
          );
        })}

        <p className="mt-10">
          {" "}
          <Link to="AddPost">
            <Button variant="outlined">Add Posts</Button>
          </Link>
        </p>
      </>
    );
  }
}
export default Dashboard;
