import { Button, TextField } from "@mui/material";
import React from "react";

class AddPost extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userPost: {
        userId: 0,
        postImage: null,
        caption: null,
        location: null,
        heartCount: 0,
      },
      userPosts: [],
    };
    this.setPostData = this.setPostData.bind(this);
    this.addPostClick = this.addPostClick.bind(this);
    this.onImageChange = this.onImageChange.bind(this);
  }

  componentDidUpdate() {
    console.log("userPosts did update: ", this.state.userPost);
  }
  addPostClick = () => {
    console.log("this.state.userPost.userId + 1: ", this.state.userPost.userId);
    // const { caption, location, userId } = this.state.userPost;
    // if (caption && location) {
