import { TextField } from "@mui/material";
import React from "react";

class AddPost extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      userPost: {
        caption: null,
        location: null,
      },
    };
    this.setPostData = this.setPostData.bind(this);
  }
  componentDidUpdate() {}
  setPostData = (e) => {
    this.setState({
      userPost: {
        ...this.state.userPost,
        [e.target.name]: e.target.value,
      },
    });
  };
  render() {
    return (
      <>
        <div className="flex flex-col">
          <h2 className="">Add Post</h2>
          <TextField
            onChange={this.setPostData}
            name="caption"
            label="Caption"
            // error={}
            helperText="Field is required"
          />
          <TextField
            onChange={this.setPostData}
            name="location"
            label="Location"
            helperText="Field is required"
          />
        </div>
      </>
    );
  }
}
export default AddPost;
